import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

const NetMovementSummary = () => {
  const { base } = useAuth();
  const [summary, setSummary] = useState(null);
  const [popup, setPopup] = useState(false);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const res = await api.get("/movements", {
          params: { base },
        });
        setSummary(res.data);
      } catch (err) {
        console.error("Error fetching movement summary:", err);
      }
    };
    if (base) fetchSummary();
  }, [base]);

  if (!summary) return <p>Loading movement summary...</p>;

  return (
    <div className="summary-card">
      <h3>Net Movement Summary for {base}</h3>
      <ul>
        <li>Opening Balance: {summary.openingBalance || 0}</li>
        <li>Closing Balance: {summary.closingBalance || 0}</li>
        <li>
          Net Movement: {summary.netMovement || 0}{" "}
          <button onClick={() => setPopup(true)}>View Details</button>
        </li>
        <li>Assigned: {summary.totalAssigned || 0}</li>
        <li>Expended: {summary.totalExpended || 0}</li>
      </ul>

      {popup && (
        <div className="popup">
          <h4>Net Movement Breakdown</h4>

          <h5>Purchases</h5>
          <ul>
            {summary.purchases && summary.purchases.length > 0 ? (
              summary.purchases.map((p) => (
                <li key={p._id}>
                  {p.assetName} - {p.quantity} (
                  {new Date(p.date).toLocaleDateString()})
                </li>
              ))
            ) : (
              <li>No purchases</li>
            )}
          </ul>

          <h5>Transfers In</h5>
          <ul>
            {summary.transferIn && summary.transferIn.length > 0 ? (
              summary.transferIn.map((t) => (
                <li key={t._id}>
                  {t.assetName} - {t.quantity} from {t.fromBase}
                </li>
              ))
            ) : (
              <li>No transfers in</li>
            )}
          </ul>

          <h5>Transfers Out</h5>
          <ul>
            {summary.transferOut && summary.transferOut.length > 0 ? (
              summary.transferOut.map((t) => (
                <li key={t._id}>
                  {t.assetName} - {t.quantity} to {t.toBase}
                </li>
              ))
            ) : (
              <li>No transfers out</li>
            )}
          </ul>

          <button onClick={() => setPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default NetMovementSummary;
