// src/components/NetMovementSummary.js

import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

const NetMovementSummary = () => {
  const { base } = useAuth();
  const [summary, setSummary] = useState(null);
  const [popup, setPopup] = useState(false);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const res = await api.get("/movements", {
          params: { base },
        });
        setSummary(res.data);
      } catch (err) {
        console.error("Error fetching movement summary:", err);
      }
    };
    if (base) fetchSummary();
  }, [base]);

  if (!summary) return <p>Loading movement summary...</p>;

  return (
    <div className="summary-card">
      <h3>Net Movement Summary for {base}</h3>
      <ul>
        <li>Opening Balance: {summary.openingBalance}</li>
        <li>Closing Balance: {summary.closingBalance}</li>
        <li>
          Net Movement: {summary.netMovement} {" "}
          <button onClick={() => setPopup(true)}>View Details</button>
        </li>
        <li>Assigned: {summary.assigned}</li>
        <li>Expended: {summary.expended}</li>
      </ul>

      {popup && (
        <div className="popup">
          <h4>Net Movement Breakdown</h4>
          <ul>
            <li>Purchases: {summary.purchases}</li>
            <li>Transfer In: {summary.transferIn}</li>
            <li>Transfer Out: {summary.transferOut}</li>
          </ul>
          <button onClick={() => setPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default NetMovementSummary;
